const amigosOriginais=[],amigos=[],eAmigoInput=document.querySelector("#amigo"),eBotaoAdicionar=document.getElementById("adicionarNome"),eAmigoList=document.querySelector("#listaNomes"),eBotaoSortear=document.getElementById("sortearAmigo"),eAmigoResultado=document.querySelector("#resultado"),eBotaoLimpar=document.querySelector("#limparLista");function adicionarAmigo(){let o=eAmigoInput.value.trim(),e=validarAmigo(o);if(e){mostrarMensagemErro(e);return}amigos.push(o),atualizarListaAmigos(),eAmigoInput.value=""}function validarAmigo(o){return o.length<3?"O nome do amigo deve ter pelo menos 3 caracteres":amigos.includes(o)?"Amigo j\xe1 adicionado":/^[A-Z]/.test(o)?null:"O nome deve iniciar com uma letra mai\xfascula"}function mostrarMensagemErro(o){window.alert(o)}function sortearAmigo(){if(0===amigos.length){window.alert("Adicione ao menos um amigo antes de sortear.");return}let o=Math.floor(Math.random()*amigos.length),e=amigos[o],i=`<span style="color: #FF6000; font-weight: bold;">Parab\xe9ns! O sorteado foi ${e}.</span>`;eAmigoResultado.innerHTML=i,amigos.splice(o,1),atualizarListaAmigos(),setTimeout(()=>{confetti({particleCount:100,spread:70,origin:{y:.6}})},1e3)}function limparLista(){amigos.length=0,atualizarListaAmigos(),eAmigoResultado.textContent=""}function atualizarListaAmigos(){eAmigoList.innerHTML="",amigos.forEach(o=>{let e=document.createElement("li");e.textContent=o,eAmigoList.appendChild(e)})}eBotaoLimpar.onclick=limparLista,eBotaoSortear.addEventListener("click",sortearAmigo),eBotaoAdicionar.addEventListener("click",adicionarAmigo);